<template>   <div     :class="{'bg-gray-900': !settings.overlay_color}"     :style="{'background-color': settings.overlay_color}"   >     <a :id="`basic-hero-banner-${index}`"></a>     <div class="relative isolate overflow-hidden pt-14">       <img         v-if="settings.image?.filename"         :srcset="`           ${getResizeUrl(settings.image, {width: 576})} 576w,           ${getResizeUrl(settings.image, {width: 768})} 768w,           ${getResizeUrl(settings.image, {width: 992})} 992w,           ${getResizeUrl(settings.image, {width: 1200})} 1200w,           ${settings.image.filename}         `"         sizes="           (max-width: 576px) 576px,           (max-width: 768px) 768px,           (max-width: 992px) 992px,           (max-width: 1200px) 1200px,           1400px         "         :src="settings.image.filename"         :alt="settings.image.attribution ?? null"         class="absolute inset-0 -z-10 h-full w-full object-cover"       >        <div         v-if="settings.overlay_color"         class="absolute z-0 top-0 left-0 h-full w-full"         :class="[           gradientClasses         ]"         :style="{'opacity': `${settings.overlay_opacity}%`}"       />        <div         :class="[           {'sm:py-48': settings.size === 'medium'},           {'sm:py-48 lg:py-56': settings.size === 'large'},           {'lg:max-w-7xl': settings.alignment !== 'center'}         ]"         class="relative mx-auto max-w-2xl z-10 py-32 px-8 lg:px-16"       >         <div :class="[           {'lg:w-2/3 xl:w-1/2': settings.alignment !== 'center'},           {'lg:mr-auto': settings.alignment === 'left'},           {'lg:ml-auto': settings.alignment === 'right'}         ]">           <div             v-if="settings.pretitle"             :class="[               {'sm:justify-center': settings.alignment === 'center'},               {'sm:justify-start': settings.alignment === 'left'},               {'sm:justify-end': settings.alignment === 'right'}             ]"             class="hidden sm:mb-8 sm:flex"           >             <div               :class="colors.text"               class="relative rounded-full px-3 py-1 text-sm leading-6 ring-1 ring-white/50"               v-text="settings.pretitle"             />           </div>            <div :class="`text-${settings.alignment}`">             <h1               v-if="settings.title"               class="text-4xl font-bold tracking-tight text-white sm:text-6xl"               v-text="settings.title"             />              <p               v-if="settings.content"               :class="colors.text"               class="mt-6 text-lg leading-8"               v-text="settings.content"             />              <div               :class="[                 {'justify-center': settings.alignment === 'center'},                 {'justify-start': settings.alignment === 'left'},                 {'justify-end': settings.alignment === 'right'}               ]"                class="mt-10 flex items-center gap-x-6"             >               <a                 v-if="settings.primary_button_text"                 :href="settings.primary_button_link ?? '#'"                 :style="[                   {'background-color': settings.primary_button_background},                   {'color': settings.primary_button_color}                 ]"                 class="rounded-md px-3.5 py-2.5 text-sm font-semibold shadow-sm focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2"                 v-text="settings.primary_button_text"                 @click="handleMenuItemClick"               />             </div>            </div>         </div>       </div>      </div>   </div> </template><script> export default {   props: {     index: {       type: Number,       default: 0     },     schema: {       type: Object,       default: () => ({})     }   },    computed: {     settings() {       const data = {};        this.schema.settings?.forEach((setting) => {         data[setting.id] = setting.value;       });        return data;     },      colors() {       return themeColors('dark');     },      gradientClasses() {       let classes = `bg-[${this.settings.overlay_color}] `;        if (this.settings.alignment !== 'center') {         classes += `lg:w-3/4 lg:bg-transparent from-[${this.settings.overlay_color}] from-50% to-transparent `;       }        if (this.settings.alignment === 'left') {         classes += 'lg:bg-gradient-to-r ';       }        if (this.settings.alignment === 'right') {         classes += 'lg:bg-gradient-to-l lg:left-auto lg:right-0';       }        return classes;     }   },    methods: {     getResizeUrl,      handleMenuItemClick(event) {       const href = event.target.getAttribute('href');        if (!href || !href.startsWith('#')) {         return;       }        event.preventDefault();       const target = document.querySelector(href);        if (!target) {         return;       }        target.scrollIntoView({         behavior: 'smooth',         block: 'start',         inline: 'nearest'       });     }   } }; </script><style></style>