<template>   <div>     <a :id="`basic-content-image-${index}`"></a>     <div :class="colors.background">       <div class="flex flex-col gap-16 mx-auto px-6 py-24 sm:py-32 lg:flex-row lg:gap-28 lg:px-8 lg:w-full lg:max-w-7xl">         <div class="lg:w-4/6 max-w-xl mx-auto lg:max-w-none flex flex-col justify-center">           <h1             v-if="settings.title"             :class="colors.text"             class="text-3xl font-bold tracking-tight sm:text-4xl"             v-text="settings.title"           />            <div             v-if="blocks.length"             :class="colors.text_light"             class="text-base leading-7"           >             <div               v-for="(block, index) in blocks"               :key="index"               :data-sgpt-block="block.id"             >               <h2                 v-if="block.header"                 :class="colors.text"                 class="mt-16 text-2xl font-bold tracking-tight"                 v-text="block.header"               />               <p                 v-if="block.content"                 class="mt-8"                 v-text="block.content"               />             </div>           </div>         </div>          <div           v-if="settings.image?.filename"           :class="{'lg:order-last': settings.alignment === 'right'}"           class="relative lg:w-2/6 order-first lg:pb-8"         >           <picture>             <source               :srcset="`${getResizeUrl(settings.image, {width: 368, height: 368, crop: true})}`"               media="(min-width: 1024px)"             />             <img               class="lg:sticky lg:top-0 mx-auto lg:w-full rounded-xl shadow-xl"               :src="`${getResizeUrl(settings.image, {width: 720, height: 464, crop: true})}`"               :alt="settings.image.attribution ?? null"             />           </picture>         </div>       </div>     </div>   </div> </template><script> export default {   props: {     index: {       type: Number,       default: 0     },     schema: {       type: Object,       default: () => ({})     }   },    computed: {     settings() {       const data = {};        this.schema.settings?.forEach((setting) => {         data[setting.id] = setting.value;       });        return data;     },      blocks() {       const blocks = this.schema.blocks?.map((block) => {         const data = {           id: block.id,           type: block.type         };          block.settings.forEach((setting) => {           data[setting.id] = setting.value;         });          return data;       });        return blocks ?? [];     },      colors() {       return themeColors(this.settings.theme);     }   },    methods: {     getResizeUrl   } }; </script><style></style>