<template>   <div>     <a :id="`three-column-images-${index}`"></a>     <div :class="colors.background" class="py-24 sm:py-32">       <div class="mx-auto max-w-7xl px-6 lg:px-8">         <div class="mx-auto max-w-2xl text-center">           <h2             v-if="settings.title"             :class="colors.text"             class="text-3xl font-bold tracking-tight sm:text-4xl"             v-text="settings.title"           />           <p             v-if="settings.content"             :class="colors.text_light"             class="mt-2 text-lg leading-8"             v-text="settings.content"           />         </div>          <div v-if="blocks.length" class="mx-auto mt-16 grid max-w-2xl grid-cols-1 gap-x-8 gap-y-20 lg:mx-0 lg:max-w-none lg:grid-cols-3">           <article             v-for="(block, index) in blocks"             :key="index"             class="flex flex-col items-start justify-between"             :data-sgpt-block="block.id"           >             <div v-if="block.image?.filename" class="relative w-full">               <img :src="getResizeUrl(block.image, {width: 672, crop: true})" :alt="block.image.attribution ?? null" class="aspect-[16/9] w-full rounded-2xl object-cover sm:aspect-[2/1] lg:aspect-[3/2]">               <div class="absolute inset-0 rounded-2xl ring-1 ring-inset ring-gray-900/10"></div>             </div>             <div class="max-w-xl">               <div class="group mt-8 relative">                 <h3                   v-if="block.title"                   :class="[                     colors.text,                     getHoverClass(block.link)                   ]"                   class="mt-3 text-lg font-semibold leading-6"                 >                   <a v-if="block.link" :href="block.link">                     <span class="absolute inset-0"></span>                     {{ block.title }}                   </a>                    <div v-else>                     {{ block.title }}                   </div>                 </h3>                 <p                   v-if="block.content"                   :class="colors.text_light"                   class="mt-5 line-clamp-3 text-sm leading-6"                   v-text="block.content"                 />               </div>             </div>           </article>         </div>       </div>     </div>   </div> </template><script> export default {   props: {     index: {       type: Number,       default: 0     },     schema: {       type: Object,       default: () => ({})     }   },    computed: {     settings() {       const data = {};        this.schema.settings?.forEach((setting) => {         data[setting.id] = setting.value;       });        return data;     },      blocks() {       const blocks = this.schema.blocks?.map((block) => {         const data = {           id: block.id,           type: block.type         };          block.settings.forEach((setting) => {           data[setting.id] = setting.value;         });          return data;       });        return blocks ?? [];     },      colors() {       return themeColors(this.settings.theme);     }   },    methods: {     getResizeUrl,      getHoverClass(link) {       if (!link) {         return null;       }        return `group-hover:${this.colors.text_light}`;     }   } } </script><style></style>